div.script:
      function selectDep(){
		var pp=$("#pays").val();
		if (pp!=""){
			$.post("/coursesRef.php", "getDep=true&paysSel="+encodeURIComponent(pp), function(resp) {
				$("#laydep").html(resp);
			});
		}
	}
	function searchRace() {
		$("#resSearchRace").html("");
		$.post("/coursesRef.php", $(document.fSearchRace).serialize(), function(resp) {
			$("#resSearchRace").html(resp);
		});
	}
